import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [authCode, setAuthCode] = useState('');
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/auth/login', { email, password });
      if (response.data.requiresCode) {
        setStep(2);
      }
    } catch (error) {
      alert('Login failed: ' + (error.response?.data?.message || 'Check credentials'));
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyCode = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/auth/verify', { email, authCode });
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('role', response.data.role);
      window.location.href = `/dashboard/${response.data.role}`;
    } catch (error) {
      alert('Verification failed: Invalid code');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div className="card" style={{ width: '400px' }}>
        <h2 style={{ marginBottom: '1.5rem', textAlign: 'center', color: '#1e3a8a' }}>SOS Tunisie Platform</h2>
        <p style={{ marginBottom: '1rem', textAlign: 'center', opacity: 0.7 }}>
          {step === 1 ? 'Enter your credentials' : 'Check your email for the 6-digit code'}
        </p>

        {step === 1 ? (
          <div>
            <input
              type="email"
              placeholder="name@sos-tunisie.org"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button
              onClick={handleLogin}
              style={{ width: '100%', background: 'var(--primary)', color: 'white' }}
              disabled={loading}
            >
              {loading ? 'Processing...' : 'Login'}
            </button>
          </div>
        ) : (
          <div>
            <input
              type="text"
              placeholder="Enter Auth Code"
              value={authCode}
              maxLength={6}
              onChange={(e) => setAuthCode(e.target.value)}
              style={{ textAlign: 'center', fontSize: '1.5rem', letterSpacing: '0.5rem' }}
            />
            <button
              onClick={handleVerifyCode}
              style={{ width: '100%', background: 'var(--success)', color: 'white' }}
              disabled={loading}
            >
              {loading ? 'Verifying...' : 'Verify Access'}
            </button>
            <button
              onClick={() => setStep(1)}
              style={{ width: '100%', marginTop: '0.5rem', background: 'transparent', color: 'var(--secondary)' }}
            >
              Back to Login
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Login;
