import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    role: '',
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('/api/auth/signup', formData);
      alert('Account created successfully! You can now login.');
      window.location.href = '/login';
    } catch (error) {
      alert('Signup failed: ' + (error.response?.data?.message || 'Unknown error'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div className="card" style={{ width: '420px' }}>
        <h2 style={{ marginBottom: '1.5rem', textAlign: 'center', color: '#1e3a8a' }}>Create Account</h2>
        <p style={{ marginBottom: '1rem', textAlign: 'center', opacity: 0.7 }}>
          Only @sos-tunisie.org emails are accepted
        </p>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="name@sos-tunisie.org"
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleChange}
            required
          />
          <select name="role" onChange={handleChange} required style={{ marginBottom: '1.5rem' }}>
            <option value="">Select Your Role</option>
            <option value="mother">Mother (MÃ¨re)</option>
            <option value="tante">Aunt (Tante)</option>
            <option value="educator">Educator</option>
            <option value="psychologist">Psychologist</option>
            <option value="director">Director</option>
          </select>
          <button
            type="submit"
            style={{ width: '100%', background: 'var(--primary)', color: 'white' }}
            disabled={loading}
          >
            {loading ? 'Creating...' : 'Sign Up'}
          </button>
        </form>
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>
          Already have an account?{' '}
          <a href="/login" style={{ color: 'var(--primary)', textDecoration: 'none', fontWeight: '600' }}>
            Login
          </a>
        </p>
      </div>
    </div>
  );
};

export default Signup;
