import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DashboardDirector = () => {
  const [reports, setReports] = useState([]);
  const [villageStats, setVillageStats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeView, setActiveView] = useState('overview'); // overview | reports

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [reportsRes, statsRes] = await Promise.all([
        axios.get('/api/reports/final'),
        axios.get('/api/reports/by-village')
      ]);
      setReports(reportsRes.data);
      setVillageStats(statsRes.data);
    } catch (error) {
      console.error('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleDecision = async (id, decision) => {
    try {
      await axios.put(`/api/reports/${id}/decision`, { decision });
      alert(`Décision enregistrée: ${decision}`);
      loadData();
    } catch (error) {
      alert('Échec');
    }
  };

  const getStatusBadge = (status) => {
    const colors = {
      en_attente: { bg: '#fef3c7', text: '#d97706', label: 'En attente' },
      en_cours: { bg: '#dbeafe', text: '#2563eb', label: 'En cours' },
      fausse_signalement: { bg: '#fee2e2', text: '#ef4444', label: 'Fausse' },
      cloture: { bg: '#dcfce7', text: '#16a34a', label: 'Clôturé' }
    };
    const s = colors[status] || colors.en_attente;
    return <span style={{ background: s.bg, color: s.text, padding: '0.2rem 0.6rem', borderRadius: '1rem', fontSize: '0.75rem', fontWeight: 'bold' }}>{s.label}</span>;
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto', width: '100%' }}>
      <h2 style={{ color: 'var(--primary)', marginBottom: '1rem', textAlign: 'center' }}>
        Niveau 3 — Gouvernance & Décision
      </h2>

      {/* Tab Nav */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: '0.5rem', marginBottom: '2rem' }}>
        <button onClick={() => setActiveView('overview')}
          style={{ background: activeView === 'overview' ? 'var(--primary)' : '#e2e8f0', color: activeView === 'overview' ? 'white' : 'var(--text)', padding: '0.6rem 1.5rem' }}>
          Vue globale par village
        </button>
        <button onClick={() => setActiveView('reports')}
          style={{ background: activeView === 'reports' ? 'var(--primary)' : '#e2e8f0', color: activeView === 'reports' ? 'white' : 'var(--text)', padding: '0.6rem 1.5rem' }}>
          Dossiers à traiter
        </button>
      </div>

      {loading ? <p style={{ textAlign: 'center' }}>Chargement...</p> : (
        <>
          {/* Village Overview */}
          {activeView === 'overview' && (
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))', gap: '1.5rem' }}>
              {villageStats.length === 0 ? <p>Aucune donnée disponible.</p> : (
                villageStats.map(v => (
                  <div key={v._id} className="card" style={{ borderTop: `4px solid ${v.critical > 0 ? '#ef4444' : 'var(--primary)'}` }}>
                    <h3 style={{ margin: '0 0 1rem 0' }}>{v._id || 'Non spécifié'}</h3>
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '0.5rem' }}>
                      <div style={{ background: '#f8fafc', padding: '0.75rem', borderRadius: '0.5rem', textAlign: 'center' }}>
                        <p style={{ fontSize: '1.5rem', fontWeight: 'bold', margin: 0, color: 'var(--primary)' }}>{v.total}</p>
                        <p style={{ fontSize: '0.75rem', opacity: 0.6, margin: 0 }}>Total</p>
                      </div>
                      <div style={{ background: '#fef2f2', padding: '0.75rem', borderRadius: '0.5rem', textAlign: 'center' }}>
                        <p style={{ fontSize: '1.5rem', fontWeight: 'bold', margin: 0, color: '#ef4444' }}>{v.critical}</p>
                        <p style={{ fontSize: '0.75rem', opacity: 0.6, margin: 0 }}>Critiques</p>
                      </div>
                      <div style={{ background: '#fef3c7', padding: '0.75rem', borderRadius: '0.5rem', textAlign: 'center' }}>
                        <p style={{ fontSize: '1.5rem', fontWeight: 'bold', margin: 0, color: '#d97706' }}>{v.enAttente}</p>
                        <p style={{ fontSize: '0.75rem', opacity: 0.6, margin: 0 }}>En attente</p>
                      </div>
                      <div style={{ background: '#dcfce7', padding: '0.75rem', borderRadius: '0.5rem', textAlign: 'center' }}>
                        <p style={{ fontSize: '1.5rem', fontWeight: 'bold', margin: 0, color: '#16a34a' }}>{v.cloture}</p>
                        <p style={{ fontSize: '0.75rem', opacity: 0.6, margin: 0 }}>Clôturés</p>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          )}

          {/* Reports Table */}
          {activeView === 'reports' && (
            <div className="card">
              {reports.length === 0 ? <p style={{ textAlign: 'center' }}>Aucun dossier à traiter.</p> : (
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr style={{ background: '#f8fafc', textAlign: 'left' }}>
                      <th style={{ padding: '0.75rem' }}>Enfant</th>
                      <th style={{ padding: '0.75rem' }}>Village</th>
                      <th style={{ padding: '0.75rem' }}>Classification</th>
                      <th style={{ padding: '0.75rem' }}>Status</th>
                      <th style={{ padding: '0.75rem' }}>Décision</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map(r => (
                      <tr key={r._id} style={{ borderBottom: '1px solid #e2e8f0' }}>
                        <td style={{ padding: '0.75rem' }}>{r.childFirstName} {r.childLastName}</td>
                        <td style={{ padding: '0.75rem' }}>{r.program}</td>
                        <td style={{ padding: '0.75rem' }}>
                          <span style={{
                            background: r.classification === 'faux' ? '#fee2e2' : r.classification === 'prise_en_charge' ? '#dbeafe' : '#dcfce7',
                            color: r.classification === 'faux' ? '#ef4444' : r.classification === 'prise_en_charge' ? '#2563eb' : '#16a34a',
                            padding: '0.2rem 0.6rem', borderRadius: '1rem', fontSize: '0.8rem', fontWeight: 'bold'
                          }}>
                            {r.classification || '—'}
                          </span>
                        </td>
                        <td style={{ padding: '0.75rem' }}>{getStatusBadge(r.status)}</td>
                        <td style={{ padding: '0.75rem' }}>
                          {r.directorDecision ? (
                            <span style={{ fontWeight: 'bold', color: 'var(--primary)' }}>{r.directorDecision}</span>
                          ) : (
                            <div style={{ display: 'flex', gap: '0.25rem' }}>
                              <button onClick={() => handleDecision(r._id, 'prise_en_charge')}
                                style={{ background: 'var(--primary)', color: 'white', padding: '0.3rem 0.5rem', fontSize: '0.75rem' }}>
                                Prise en charge
                              </button>
                              <button onClick={() => handleDecision(r._id, 'sanction')}
                                style={{ background: '#f97316', color: 'white', padding: '0.3rem 0.5rem', fontSize: '0.75rem' }}>
                                Sanction
                              </button>
                              <button onClick={() => handleDecision(r._id, 'suivi')}
                                style={{ background: 'var(--success)', color: 'white', padding: '0.3rem 0.5rem', fontSize: '0.75rem' }}>
                                Suivi
                              </button>
                              <button onClick={() => handleDecision(r._id, 'archive')}
                                style={{ background: 'var(--secondary)', color: 'white', padding: '0.3rem 0.5rem', fontSize: '0.75rem' }}>
                                Archiver
                              </button>
                            </div>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default DashboardDirector;
