import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DashboardDirector = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFinalReports();
  }, []);

  const fetchFinalReports = async () => {
    try {
      const response = await axios.get('/api/reports/final');
      setReports(response.data);
    } catch (error) {
      console.error('Failed to fetch reports');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '1000px', margin: '0 auto', width: '100%' }}>
      <div className="card">
        <h2 style={{ color: 'var(--primary)', marginBottom: '1.5rem', textAlign: 'center' }}>Director Oversight Dashboard</h2>
        <p style={{ textAlign: 'center', marginBottom: '2rem', opacity: 0.7 }}>Review final decisions and validated reports from the clinical team.</p>

        {loading ? <p style={{ textAlign: 'center' }}>Loading reports...</p> : reports.length === 0 ? <p style={{ textAlign: 'center' }}>No reports pending review.</p> : (
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ background: '#f8fafc', textAlign: 'left' }}>
                <th style={{ padding: '1rem', borderBottom: '2px solid #e2e8f0' }}>Child Name</th>
                <th style={{ padding: '1rem', borderBottom: '2px solid #e2e8f0' }}>Village</th>
                <th style={{ padding: '1rem', borderBottom: '2px solid #e2e8f0' }}>Decision</th>
                <th style={{ padding: '1rem', borderBottom: '2px solid #e2e8f0' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reports.map(report => (
                <tr key={report._id} style={{ borderBottom: '1px solid #e2e8f0' }}>
                  <td style={{ padding: '1rem' }}>{report.childName}</td>
                  <td style={{ padding: '1rem' }}>{report.program}</td>
                  <td style={{ padding: '1rem' }}>
                    <span style={{
                      background: report.classification === 'faux' ? '#fee2e2' : '#dcfce7',
                      color: report.classification === 'faux' ? '#ef4444' : '#22c55e',
                      padding: '0.25rem 0.75rem',
                      borderRadius: '1rem',
                      fontSize: '0.85rem',
                      fontWeight: 'bold'
                    }}>
                      {report.classification}
                    </span>
                  </td>
                  <td style={{ padding: '1rem' }}>
                    <button style={{ background: 'var(--primary)', color: 'white', padding: '0.5rem 1rem' }}>
                      Audit Report
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default DashboardDirector;
