import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TopBar from '../components/TopBar';

const DashboardDirector = () => {
  const [reports, setReports] = useState([]);
  const [villageStats, setVillageStats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeView, setActiveView] = useState('overview');
  const [filterVillage, setFilterVillage] = useState('All');

  useEffect(() => { loadData(); }, []);

  const loadData = async () => {
    try {
      const [reportsRes, statsRes] = await Promise.all([
        axios.get('/api/reports/final'),
        axios.get('/api/reports/by-village')
      ]);
      setReports(reportsRes.data);
      setVillageStats(statsRes.data);
    } catch (error) { console.error('Error loading data'); }
    finally { setLoading(false); }
  };

  const handleDecision = async (id, decision) => {
    if (!window.confirm(`Confirmer la d√©cision : ${decision} ?`)) return;
    try {
      await axios.put(`/api/reports/${id}/decision`, { decision });
      loadData();
    } catch (error) { alert('‚ùå √âchec'); }
  };

  const getStatusBadge = (r) => {
    if (r.status === 'overdue') return <span className="badge badge-danger">‚ö†Ô∏è RETARD INVALIDANT</span>;
    if (r.deadline && new Date(r.deadline) < new Date() && r.status === 'en_cours') return <span className="badge badge-danger">‚ö†Ô∏è D√âLAI D√âPASS√â</span>;

    const map = { en_attente: 'badge-warning', en_cours: 'badge-info', cloture: 'badge-success' };
    return <span className={`badge ${map[r.status] || 'badge-neutral'}`}>{r.status.replace('_', ' ')}</span>;
  };

  const filteredReports = reports.filter(r => filterVillage === 'All' || r.village === filterVillage);

  return (
    <div className="app-layout">
      <TopBar role="director" />
      <div className="page-content">

        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <div>
            <h1 style={{ fontSize: '1.8rem', color: 'var(--primary-dark)' }}>Tableau de bord Direction</h1>
            <p className="subtitle">Supervision nationale et prise de d√©cision</p>
          </div>
          <div className="tabs" style={{ margin: 0 }}>
            <button className={`tab ${activeView === 'overview' ? 'active' : ''}`} onClick={() => setActiveView('overview')}>Vue Globale</button>
            <button className={`tab ${activeView === 'reports' ? 'active' : ''}`} onClick={() => setActiveView('reports')}>Dossiers √† traiter</button>
          </div>
        </div>

        {loading ? <p style={{ textAlign: 'center' }}>Chargement des donn√©es...</p> : (
          <div className="animate-in">
            {activeView === 'overview' && (
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '1.5rem' }}>
                {villageStats.map(v => (
                  <div key={v._id} className={`village-card ${v.critical > 0 ? 'critical' : ''}`}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
                      <h3 style={{ fontSize: '1.2rem' }}>{v._id || 'Village inconnu'}</h3>
                      {v.critical > 0 && <span className="badge badge-danger">‚ö†Ô∏è {v.critical} critiques</span>}
                    </div>

                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '0.75rem' }}>
                      <div style={{ background: 'var(--bg)', padding: '1rem', borderRadius: 'var(--radius-sm)', textAlign: 'center' }}>
                        <div style={{ fontSize: '1.5rem', fontWeight: 800, color: 'var(--primary)' }}>{v.total}</div>
                        <div style={{ fontSize: '0.8rem', color: 'var(--text-muted)' }}>Total</div>
                      </div>
                      <div style={{ background: 'var(--success-light)', padding: '1rem', borderRadius: 'var(--radius-sm)', textAlign: 'center' }}>
                        <div style={{ fontSize: '1.5rem', fontWeight: 800, color: 'var(--success)' }}>{v.cloture}</div>
                        <div style={{ fontSize: '0.8rem', color: 'var(--success)' }}>Cl√¥tur√©s</div>
                      </div>
                    </div>

                    <div style={{ marginTop: '1rem', paddingTop: '1rem', borderTop: '1px solid var(--border-light)' }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.9rem' }}>
                        <span>En attente:</span>
                        <span style={{ fontWeight: 600 }}>{v.enAttente}</span>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.9rem', marginTop: '0.25rem' }}>
                        <span>En cours:</span>
                        <span style={{ fontWeight: 600 }}>{v.enCours}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {activeView === 'reports' && (
              <div className="card">
                <div className="card-header" style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <h3>Dossiers n√©cessitant une d√©cision</h3>
                  <select
                    value={filterVillage}
                    onChange={(e) => setFilterVillage(e.target.value)}
                    style={{ padding: '0.5rem', borderRadius: 'var(--radius-sm)', border: '1px solid var(--border-light)' }}
                  >
                    <option value="All">Tous les villages</option>
                    <option value="Mahras">Mahras</option>
                    <option value="Gammarth">Gammarth</option>
                    <option value="Siliana">Siliana</option>
                    <option value="Akouda">Akouda</option>
                  </select>
                </div>
                {filteredReports.length === 0 ? <div className="empty-state">Tout est √† jour ! ‚úÖ</div> : (
                  <table>
                    <thead>
                      <tr>
                        <th>Enfant</th>
                        <th>Village</th>
                        <th>Classification</th>
                        <th>Status</th>
                        <th>D√©cision Direction</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredReports.filter(r => !r.archived).map(r => (
                        <tr key={r._id} style={{
                          background: (r.status === 'overdue' || (r.deadline && new Date(r.deadline) < new Date() && r.status === 'en_cours')) ? '#fef2f2' : 'transparent'
                        }}>
                          <td style={{ fontWeight: 600 }}>
                            {r.childFirstName} {r.childLastName}
                            {(r.status === 'overdue' || (r.deadline && new Date(r.deadline) < new Date() && r.status === 'en_cours')) && (
                              <div style={{ fontSize: '0.7rem', color: 'var(--danger)', fontWeight: 700 }}>‚ö†Ô∏è RETARD SIGNAL√â</div>
                            )}
                          </td>
                          <td>{r.village}</td>
                          <td>
                            <span className={`badge ${r.classification === 'faux' ? 'badge-danger' : 'badge-info'}`}>
                              {r.classification || '-'}
                            </span>
                          </td>
                          <td>{getStatusBadge(r)}</td>
                          <td>
                            {r.directorDecision ? (
                              <span style={{ fontWeight: 700, color: 'var(--primary)' }}>üîí {r.directorDecision}</span>
                            ) : (
                              <div style={{ display: 'flex', gap: '0.5rem' }}>
                                <button className="btn-primary btn-sm" onClick={() => handleDecision(r._id, 'prise_en_charge')}>Prise en charge</button>
                                <button className="btn-warning btn-sm" onClick={() => handleDecision(r._id, 'sanction')}>Sanction</button>
                                <button className="btn-secondary btn-sm" onClick={() => handleDecision(r._id, 'suivi')}>Suivi</button>
                                <button className="btn-ghost btn-sm" onClick={() => handleDecision(r._id, 'archive')}>Archiver</button>
                              </div>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardDirector;
