import React, { useState } from 'react';
import axios from 'axios';

const DashboardMother = () => {
  const [report, setReport] = useState({
    anonymous: false,
    program: '',
    abuserFirstName: '',
    abuserLastName: '',
    childFirstName: '',
    childLastName: '',
    incidentDescription: '',
    incidentType: '',
    urgency: '',
    attachments: []
  });
  const [submitting, setSubmitting] = useState(false);
  const [myReports, setMyReports] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  const handleChange = (e) => {
    setReport({ ...report, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setReport({ ...report, attachments: e.target.files });
  };

  const loadMyReports = async () => {
    try {
      const res = await axios.get('/api/reports/my-reports');
      setMyReports(res.data);
      setShowHistory(true);
    } catch (e) { console.error(e); }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    const formData = new FormData();
    Object.keys(report).forEach(key => {
      if (key === 'attachments') {
        for (let file of report.attachments) {
          formData.append('attachments', file);
        }
      } else {
        formData.append(key, report[key]);
      }
    });

    try {
      await axios.post('/api/reports/create', formData);
      alert('Signalement soumis avec succès !');
      setReport({
        anonymous: false, program: '', abuserFirstName: '', abuserLastName: '',
        childFirstName: '', childLastName: '', incidentDescription: '',
        incidentType: '', urgency: '', attachments: []
      });
    } catch (error) {
      alert('Échec: ' + (error.response?.data?.message || 'Erreur'));
    } finally {
      setSubmitting(false);
    }
  };

  const getStatusBadge = (status) => {
    const colors = {
      'en_attente': { bg: '#fef3c7', text: '#d97706', label: 'En attente' },
      'en_cours': { bg: '#dbeafe', text: '#2563eb', label: 'En cours' },
      'fausse_signalement': { bg: '#fee2e2', text: '#ef4444', label: 'Fausse' },
      'cloture': { bg: '#dcfce7', text: '#16a34a', label: 'Clôturé' }
    };
    const s = colors[status] || colors['en_attente'];
    return (
      <span style={{ background: s.bg, color: s.text, padding: '0.2rem 0.6rem', borderRadius: '1rem', fontSize: '0.75rem', fontWeight: 'bold' }}>
        {s.label}
      </span>
    );
  };

  return (
    <div style={{ maxWidth: '900px', margin: '2rem auto', padding: '1rem' }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
        <h2 style={{ color: 'var(--primary)' }}>Niveau 1 — Signalement</h2>
        <button onClick={loadMyReports} style={{ background: 'var(--secondary)', color: 'white' }}>
          {showHistory ? 'Masquer historique' : 'Mes signalements'}
        </button>
      </div>

      {/* Report History */}
      {showHistory && (
        <div className="card" style={{ marginBottom: '1.5rem' }}>
          <h3 style={{ marginBottom: '1rem' }}>Historique des signalements</h3>
          {myReports.length === 0 ? <p>Aucun signalement.</p> : (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ background: '#f8fafc', textAlign: 'left' }}>
                  <th style={{ padding: '0.75rem' }}>Enfant</th>
                  <th style={{ padding: '0.75rem' }}>Type</th>
                  <th style={{ padding: '0.75rem' }}>Urgence</th>
                  <th style={{ padding: '0.75rem' }}>Status</th>
                  <th style={{ padding: '0.75rem' }}>Date</th>
                </tr>
              </thead>
              <tbody>
                {myReports.map(r => (
                  <tr key={r._id} style={{ borderBottom: '1px solid #e2e8f0' }}>
                    <td style={{ padding: '0.75rem' }}>{r.childFirstName} {r.childLastName}</td>
                    <td style={{ padding: '0.75rem' }}>{r.incidentType}</td>
                    <td style={{ padding: '0.75rem' }}>{r.urgency}</td>
                    <td style={{ padding: '0.75rem' }}>{getStatusBadge(r.status)}</td>
                    <td style={{ padding: '0.75rem' }}>{new Date(r.createdAt).toLocaleDateString('fr-FR')}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}

      {/* Signalement Form */}
      <div className="card">
        <h3 style={{ marginBottom: '1.5rem', borderBottom: '2px solid #e2e8f0', paddingBottom: '0.5rem' }}>
          Créer un signalement
        </h3>
        <form onSubmit={handleSubmit}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '1rem', cursor: 'pointer' }}>
                <input type="checkbox" name="anonymous" checked={report.anonymous}
                  onChange={(e) => setReport({ ...report, anonymous: e.target.checked })}
                  style={{ width: 'auto', margin: 0 }} />
                Signalement anonyme
              </label>
              <input type="text" name="program" placeholder="Programme (Village SOS)" value={report.program} onChange={handleChange} required />
              <input type="text" name="abuserFirstName" placeholder="Prénom de l'abuseur" value={report.abuserFirstName} onChange={handleChange} />
              <input type="text" name="abuserLastName" placeholder="Nom de l'abuseur" value={report.abuserLastName} onChange={handleChange} />
              <input type="text" name="childFirstName" placeholder="Prénom de l'enfant" value={report.childFirstName} onChange={handleChange} required />
              <input type="text" name="childLastName" placeholder="Nom de l'enfant" value={report.childLastName} onChange={handleChange} required />
            </div>
            <div>
              <select name="incidentType" value={report.incidentType} onChange={handleChange} required>
                <option value="">Type d'incident</option>
                <option value="health">Santé</option>
                <option value="behavior">Comportement</option>
                <option value="violence">Violence</option>
                <option value="neglect">Négligence</option>
                <option value="sexual">Abus sexuel</option>
                <option value="other">Autre</option>
              </select>
              <select name="urgency" value={report.urgency} onChange={handleChange} required>
                <option value="">Niveau d'urgence</option>
                <option value="low">Faible</option>
                <option value="medium">Moyen</option>
                <option value="high">Élevé</option>
                <option value="critical">Critique</option>
              </select>
              <label style={{ fontSize: '0.9rem', opacity: 0.7, display: 'block', marginBottom: '0.5rem' }}>
                Pièces jointes (photo, audio, vidéo)
              </label>
              <input type="file" multiple onChange={handleFileChange}
                accept="image/*,audio/*,video/*"
                style={{ border: '1px dashed #cbd5e1', padding: '1rem', background: 'white' }} />
            </div>
          </div>

          <textarea name="incidentDescription" placeholder="Description libre de l'incident..."
            value={report.incidentDescription} onChange={handleChange}
            style={{ width: '100%', height: '120px', padding: '0.75rem', borderRadius: '0.5rem', border: '1px solid #e2e8f0', marginTop: '1rem', marginBottom: '1.5rem' }}
            required />

          <button type="submit"
            style={{ width: '100%', background: 'var(--primary)', color: 'white', fontSize: '1.1rem' }}
            disabled={submitting}>
            {submitting ? 'Analyse IA en cours...' : 'Soumettre le signalement'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default DashboardMother;
