import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TopBar from '../components/TopBar';

const DashboardMother = () => {
  const [report, setReport] = useState({
    anonymous: false, program: '', abuserFirstName: '', abuserLastName: '',
    childFirstName: '', childLastName: '', incidentDescription: '',
    incidentType: '', urgency: '', attachments: []
  });
  const [submitting, setSubmitting] = useState(false);
  const [myReports, setMyReports] = useState([]);
  const [view, setView] = useState('form'); // form | history

  useEffect(() => { loadMyReports(); }, []);

  const loadMyReports = async () => {
    try {
      const res = await axios.get('/api/reports/my-reports');
      setMyReports(res.data);
    } catch (e) { console.error(e); }
  };

  const handleChange = (e) => setReport({ ...report, [e.target.name]: e.target.value });
  const handleFileChange = (e) => setReport({ ...report, attachments: e.target.files });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    const formData = new FormData();
    Object.keys(report).forEach(key => {
      if (key === 'attachments') {
        for (let file of report.attachments) formData.append('attachments', file);
      } else formData.append(key, report[key]);
    });
    try {
      await axios.post('/api/reports/create', formData);
      alert('âœ… Signalement soumis avec succÃ¨s !');
      setReport({
        anonymous: false, program: '', abuserFirstName: '', abuserLastName: '',
        childFirstName: '', childLastName: '', incidentDescription: '',
        incidentType: '', urgency: '', attachments: []
      });
      loadMyReports();
    } catch (error) {
      alert('âŒ Ã‰chec: ' + (error.response?.data?.message || 'Erreur'));
    } finally { setSubmitting(false); }
  };

  const getBadge = (status) => {
    const map = {
      en_attente: { cls: 'badge-warning', label: 'â³ En attente' },
      en_cours: { cls: 'badge-info', label: 'ğŸ”„ En cours' },
      fausse_signalement: { cls: 'badge-danger', label: 'âŒ Fausse' },
      cloture: { cls: 'badge-success', label: 'âœ… ClÃ´turÃ©' }
    };
    const b = map[status] || map.en_attente;
    return <span className={`badge ${b.cls}`}>{b.label}</span>;
  };

  const getUrgencyBadge = (u) => {
    const map = { low: 'badge-neutral', medium: 'badge-warning', high: 'badge-danger', critical: 'badge-danger' };
    return <span className={`badge ${map[u] || 'badge-neutral'}`}>{u}</span>;
  };

  const stats = {
    total: myReports.length,
    pending: myReports.filter(r => r.status === 'en_attente').length,
    inProgress: myReports.filter(r => r.status === 'en_cours').length,
    closed: myReports.filter(r => r.status === 'cloture').length
  };

  return (
    <div className="app-layout">
      <TopBar role={localStorage.getItem('role') || 'mother'} />
      <div className="page-content">
        {/* Stats */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon" style={{ background: '#eff6ff', color: '#2563eb' }}>ğŸ“‹</div>
            <div className="stat-info">
              <div className="stat-value">{stats.total}</div>
              <div className="stat-label">Total signalements</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon" style={{ background: '#fef3c7', color: '#d97706' }}>â³</div>
            <div className="stat-info">
              <div className="stat-value">{stats.pending}</div>
              <div className="stat-label">En attente</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon" style={{ background: '#dbeafe', color: '#2563eb' }}>ğŸ”„</div>
            <div className="stat-info">
              <div className="stat-value">{stats.inProgress}</div>
              <div className="stat-label">En cours</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon" style={{ background: '#d1fae5', color: '#059669' }}>âœ…</div>
            <div className="stat-info">
              <div className="stat-value">{stats.closed}</div>
              <div className="stat-label">ClÃ´turÃ©s</div>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="tabs" style={{ maxWidth: '340px', marginBottom: '1.5rem' }}>
          <button className={`tab ${view === 'form' ? 'active' : ''}`} onClick={() => setView('form')}>
            Nouveau signalement
          </button>
          <button className={`tab ${view === 'history' ? 'active' : ''}`} onClick={() => setView('history')}>
            Historique
          </button>
        </div>

        {/* Form View */}
        {view === 'form' && (
          <div className="card animate-in">
            <div className="card-header">
              <div>
                <h3>CrÃ©er un signalement</h3>
                <p className="subtitle">Remplissez le formulaire pour signaler un incident</p>
              </div>
            </div>
            <form onSubmit={handleSubmit}>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.25rem' }}>
                {/* Left Column */}
                <div>
                  <div className="form-group">
                    <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', cursor: 'pointer', marginBottom: '1rem' }}>
                      <input type="checkbox" checked={report.anonymous}
                        onChange={(e) => setReport({ ...report, anonymous: e.target.checked })}
                        style={{ width: 'auto', margin: 0, accentColor: 'var(--primary)' }} />
                      <span style={{ fontSize: '0.9rem' }}>Signalement anonyme</span>
                    </label>
                  </div>
                  <div className="form-group">
                    <label>Village SOS (Lieu) *</label>
                    <select name="program" value={report.program} onChange={handleChange} required>
                      <option value="">SÃ©lectionner le village...</option>
                      <option value="Mahras">Mahras</option>
                      <option value="Gammarth">Gammarth</option>
                      <option value="Siliana">Siliana</option>
                      <option value="Akouda">Akouda</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>PrÃ©nom de l'abuseur</label>
                    <input name="abuserFirstName" placeholder="PrÃ©nom" value={report.abuserFirstName} onChange={handleChange} />
                  </div>
                  <div className="form-group">
                    <label>Nom de l'abuseur</label>
                    <input name="abuserLastName" placeholder="Nom" value={report.abuserLastName} onChange={handleChange} />
                  </div>
                </div>

                {/* Right Column */}
                <div>
                  <div className="form-group">
                    <label>PrÃ©nom de l'enfant *</label>
                    <input name="childFirstName" placeholder="PrÃ©nom" value={report.childFirstName} onChange={handleChange} required />
                  </div>
                  <div className="form-group">
                    <label>Nom de l'enfant *</label>
                    <input name="childLastName" placeholder="Nom" value={report.childLastName} onChange={handleChange} required />
                  </div>
                  <div className="form-group">
                    <label>Type d'incident *</label>
                    <select name="incidentType" value={report.incidentType} onChange={handleChange} required>
                      <option value="">SÃ©lectionner...</option>
                      <option value="health">ğŸ¥ SantÃ©</option>
                      <option value="behavior">ğŸ§  Comportement</option>
                      <option value="violence">âš ï¸ Violence</option>
                      <option value="neglect">ğŸš« NÃ©gligence</option>
                      <option value="sexual">ğŸ”´ Abus sexuel</option>
                      <option value="other">ğŸ“Œ Autre</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Niveau d'urgence *</label>
                    <select name="urgency" value={report.urgency} onChange={handleChange} required>
                      <option value="">SÃ©lectionner...</option>
                      <option value="low">ğŸŸ¢ Faible</option>
                      <option value="medium">ğŸŸ¡ Moyen</option>
                      <option value="high">ğŸŸ  Ã‰levÃ©</option>
                      <option value="critical">ğŸ”´ Critique</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Full Width Fields */}
              <div className="form-group" style={{ marginTop: '0.5rem' }}>
                <label>Description libre de l'incident *</label>
                <textarea name="incidentDescription" placeholder="DÃ©crivez l'incident en dÃ©tail..."
                  value={report.incidentDescription} onChange={handleChange}
                  style={{ height: '120px', resize: 'vertical' }} required />
              </div>

              <div className="form-group">
                <label>PiÃ¨ces jointes (photo, audio, vidÃ©o)</label>
                <div className="file-upload">
                  <p style={{ fontSize: '0.9rem', color: 'var(--text-muted)', marginBottom: '0.5rem' }}>ğŸ“ Glisser ou cliquer pour ajouter des fichiers</p>
                  <input type="file" multiple onChange={handleFileChange} accept="image/*,audio/*,video/*" />
                </div>
              </div>

              <button type="submit" className="btn-primary btn-lg" style={{ width: '100%' }} disabled={submitting}>
                {submitting ? 'â³ Analyse IA en cours...' : 'ğŸ›¡ï¸ Soumettre le signalement'}
              </button>
            </form>
          </div>
        )}

        {/* History View */}
        {view === 'history' && (
          <div className="card animate-in">
            <div className="card-header">
              <div>
                <h3>Historique des signalements</h3>
                <p className="subtitle">{myReports.length} signalement(s) trouvÃ©(s)</p>
              </div>
            </div>
            {myReports.length === 0 ? (
              <div className="empty-state">
                <div className="icon">ğŸ“­</div>
                <p>Aucun signalement soumis</p>
              </div>
            ) : (
              <table>
                <thead>
                  <tr>
                    <th>Enfant</th>
                    <th>Type</th>
                    <th>Urgence</th>
                    <th>Status</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {myReports.map(r => (
                    <tr key={r._id}>
                      <td style={{ fontWeight: 600 }}>{r.childFirstName} {r.childLastName}</td>
                      <td>{r.incidentType}</td>
                      <td>{getUrgencyBadge(r.urgency)}</td>
                      <td>{getBadge(r.status)}</td>
                      <td style={{ color: 'var(--text-muted)' }}>{new Date(r.createdAt).toLocaleDateString('fr-FR')}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardMother;
